-- productos, tabla de
-- abreviacion PRD

CREATE TABLE public.productos (
    id bigint NOT NULL,
    nombre TEXT NOT NULL UNIQUE,
    caracteristicas text,
    categoria_id bigint NOT NULL,
    marca_id bigint NOT NULL,
    unidad_id bigint NOT NULL,
    fabrica_id bigint NOT NULL,
    contenido text NOT NULL,
    cantidad integer DEFAULT 1 NOT NULL,
    fraccionable boolean DEFAULT false NOT NULL,
    barras TEXT,
    activo boolean DEFAULT true NOT NULL,
    created_at timestamp without time zone NOT NULL,
    updated_at timestamp without time zone NOT NULL,
    constraint productos_pkey primary key (id)
);

COMMENT ON COLUMN public.productos.barras
    IS 'codigo de barras';

COMMENT ON COLUMN public.productos.contenido
    IS 'descripcion del contenido, ej 12 bot 650 cc';

COMMENT ON COLUMN public.productos.caracteristicas
    IS 'caracteristicas adicionales pe: NR= no retornable, vidrio, etc.';

COMMENT ON COLUMN public.productos.fraccionable
    IS 'si el producto se vende a granel';
    
COMMENT ON COLUMN public.productos.cantidad
    IS 'cantidad de items base que este producto contiene, si es 1 es el producto base';

COMMENT ON COLUMN public.productos.fabrica_id
    IS 'fabricante del producto';

ALTER TABLE public.productos OWNER TO erp;

CREATE SEQUENCE public.productos_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1;

ALTER TABLE public.productos_id_seq OWNER TO erp;

ALTER SEQUENCE public.productos_id_seq OWNED BY public.productos.id;

ALTER TABLE ONLY public.productos ALTER COLUMN id SET DEFAULT nextval('public.productos_id_seq'::regclass);

