-- constraints
CREATE INDEX index_productos_on_categoria_id ON public.productos USING btree (categoria_id);
ALTER TABLE ONLY public.productos
    ADD CONSTRAINT fk_prd_ctg FOREIGN KEY (categoria_id) REFERENCES public.categorias(id);

CREATE INDEX index_productos_on_marca_id ON public.productos USING btree (marca_id);
ALTER TABLE ONLY public.productos
    ADD CONSTRAINT fk_prd_mrc FOREIGN KEY (marca_id) REFERENCES public.marcas(id);

CREATE INDEX index_productos_on_unidad_id ON public.productos USING btree (unidad_id);
ALTER TABLE ONLY public.productos
    ADD CONSTRAINT fk_prd_und FOREIGN KEY (unidad_id) REFERENCES public.unidades(id);

CREATE INDEX index_productos_on_fabrica_id ON public.productos USING btree (fabrica_id);
ALTER TABLE ONLY public.productos
    ADD CONSTRAINT fk_prd_fbc FOREIGN KEY (fabrica_id) REFERENCES public.fabricas(id);

ALTER TABLE public.productos
   ADD CONSTRAINT uk_prd_nombre UNIQUE (categoria_id, caracteristicas, marca_id, unidad_id, fabrica_id, contenido, cantidad);

-- triggers
-- trigger para preparar el update
CREATE OR REPLACE FUNCTION productos_insert_prepara() 
   RETURNS TRIGGER 
   LANGUAGE PLPGSQL
AS $$
BEGIN
	NEW.created_at = now();
	NEW.updated_at = now();
    RETURN NEW;
END;
$$;

CREATE TRIGGER insert_prepara
    BEFORE INSERT ON productos
    FOR EACH ROW
    EXECUTE FUNCTION productos_insert_prepara();

-- trigger para preparar el update
CREATE OR REPLACE FUNCTION productos_update_prepara() 
   RETURNS TRIGGER 
   LANGUAGE PLPGSQL
AS $$
BEGIN
	NEW.updated_at = now();
    RETURN NEW;
END;
$$;

CREATE TRIGGER update_prepara
    BEFORE UPDATE ON productos
    FOR EACH ROW
    EXECUTE FUNCTION productos_update_prepara();
